MultiBot.newPlayers = function(pParent, pX, pY, pSize)	local frame = CreateFrame("Frame", nil, pParent)	frame.players = {}	frame.index = {}		frame.button = nil		frame.parent = pParent	frame.size = pSize		frame:SetPoint("BOTTOMRIGHT", pX, pY)	frame:SetSize(pSize, pSize)	frame:Show()		-- ADD --		frame.addPlayer = function(pName, pClass, pIndex)		if(frame.players[pName] == nil) then frame.players[pName] = MultiBot.newPlayer(frame, pName, pClass, 0, (frame.size + 2) * pIndex, frame.size) end		return frame.players[pName]	end		-- SET --		frame.setButton = function(pConfig, pStrate)		if(frame.button ~= nil) then frame.button:Hide() end		frame.button = MultiBot.newDouble(frame, 0, 0, pConfig, pStrate)		return frame.button	end		frame.setState = function(pState)		frame.button.setState(pState)	end		frame.setPlayers = function(pRoster)		local tList = MultiBot.doSplit(pRoster, ", ")				for i = 1, table.getn(tList) do			local tFrom, tTo = string.find(tList[i], " ", 1)			local tName = string.sub(tList[i], 2, tFrom - 1)			local tClass = string.sub(tList[i], tTo + 1)			local tOnline = string.sub(tList[i], 1, 1)						if(MultiBot.friends.getBotByName(tName) == nil) then				frame.index[i] = tName								frame				.addPlayer(tName, tClass, i)				.setButton({					"TOGGLE:PLAYERS",					tName,					"Interface\\AddOns\\MultiBot\\Icons\\class_" .. strlower(tClass) .. ".blp",					".playerbot bot remove " .. tName,					".playerbot bot add " .. tName,					"Left to show or hide Options | Right to logout " .. tName,					tClass .. " - " .. tName,					tName				})			end		end				return frame	end		-- GET --		frame.getBotByIndex = function(pIndex)		if(frame.index[pIndex] == nil) then return nil end		return frame.players[frame.index[pIndex]]	end		frame.getBotByName = function(pName)		return frame.players[pName]	end		-- DO --		frame.doCloseInventories = function(pName)		for key, value in pairs(frame.players) do			if(value.name ~= pName and value.button.state == true) then				value.right.buttons["inventory"].setState(false)				value.inventory.doClose()			end		end	end		frame.doSummon = function()		for key, value in pairs(frame.players) do			if(value.button.state == true) then				SendChatMessage("summon", "WHISPER", nil, key)			end		end	end		frame.doHide = function()		for key, value in pairs(frame.players) do value:Hide() end	end		frame.doShow = function()		for key, value in pairs(frame.players) do value:Show() end	end		return frameendprint("AfterMultiBotPlayers")